mapper.update:

UPDATE
    CATEGORY
SET type_cd = #{catType}
WHERE 1=1
    AND user_id = #{userId}
    AND cat_id = #{catId}

mapper.select:
SELECT A.menu_id, B.menu_nm, A.menu_cost, A.sale_quantity FROM SALES A INNER JOIN MENUS B ON A.menu_id = B.menu_id WHERE 1=1 AND A.user_id = #{userId}
AND A.sale_ym = #{saleYm}
AND A.cat_id = #{catId}

SELECT
cat_id AS menuId
FROM CATEGORY
WHERE user_id=#{userId}
AND cat_nm=#{catNm}

SELECT
    menu_id AS menuId
FROM MENUS
WHERE user_id=#{userId}
AND menu_nm=#{menuNm}


SELECT
    COUNT(*)
FROM SALES
WHERE 1=1


AND user_id = #{userId}


AND sale_ym = #{saleYm}


AND cat_id = #{catId}

SELECT cat_id AS catId, cat_nm AS catNm FROM CATEGORY WHERE 1=1 AND type_cd IS NULL
AND cat_id = #{catId}
AND user_id = #{userId}
