mapper.select:

SELECT
	COUNT(*) AS size
FROM SALES A
WHERE A.user_id = #{userId}
    AND A.sale_ym = #{saleYm}
    AND A.cat_id = #{catId}
ORDER BY A.menu_cost DESC


SELECT
    SUM(A.sale_quantity) AS totalCnt, SUM(A.sale_quantity*A.menu_cost) AS totalSale
FROM
(
    SELECT sale_quantity, menu_cost
    FROM SALES
    WHERE 1=1



    AND cat_id = #{catId}




    AND user_id = #{userId}




    AND sale_ym = #{saleYm}




    LIMIT #{size}



) A
