mapper.select:

SELECT
	COUNT(*) AS size
FROM SALES A
INNER JOIN MENUS B
    ON A.menu_id = B.menu_id
WHERE A.user_id = #{userId}
    AND A.sale_ym = #{saleYm}
    AND A.cat_id = #{catId}
ORDER BY B.menu_cost DESC


SELECT
    COUNT(*)
FROM SALES
WHERE 1=1


AND user_id = #{userId}


AND sale_ym = #{saleYm}


AND cat_id = #{catId}


SELECT
    cat_id AS catId,
    cat_nm AS catNm
FROM CATEGORY
WHERE 1=1


AND cat_id = #{catId}


AND sale_ym = #{saleYm}


AND user_id = #{userId}


SELECT
    SUM(A.sale_quantity) AS totalCnt, SUM(A.sale_quantity*A.menu_cost) AS totalSale
FROM
(
    SELECT sale_quantity, menu_cost
    FROM SALES
    WHERE 1=1



    AND cat_id = #{catId}




    AND user_id = #{userId}




    AND sale_ym = #{saleYm}




    LIMIT #{size}



) A
